// function will check the temperature and change state for Yelp API call

import React from 'react';

const Conditions = (props) => {
    return (
        <div>
        {props.responseObj.main.temp >= 80 ? 
        <div className="warm">
            <p>It's warm outside. Would you like recommendations for outdoor spots?</p>
        </div>
        : <p>It's cold outside. Would you like recommendations for indoor spots?</p>}
        </div>
    )
}

export default Conditions;
