import React, {Component} from 'react';

require('dotenv').config();

class ColdTemp extends Component {
    state = {
        warmTemp: null,
        loading: false,
        data: [],
    }

    componentDidMount() {
        this.setState({loading: true})
		fetch(`https://api.yelp.com/v3/businesses/search?accessToken=${process.env.REACT_APP_YELP_API_KEY}&term=restaurants&location=${this.cityName}`)
			.then(data => data.json())
			.then(data => this.setState({data, loading: false}))
    }


    render () {
        return (
            <div className="loading-data">
                {this.state.loading 
                ? "loading recommendations for indoor spots..."
                : <div>
                </div> }

            </div>
        )
    }

}

export default ColdTemp;
